import { Response } from 'express';
import { ApiResponse, PaginatedResponse, PaginatedResult } from '../types/index.js';

export const formatResponse = <T>(data: T): ApiResponse<T> => {
  return {
    success: true,
    data,
  };
};

export const formatPaginatedResponse = <T>(result: PaginatedResult<T>): PaginatedResponse<T> => {
  return {
    success: true,
    data: result.data,
    pagination: {
      total: result.total,
      limit: result.limit,
      offset: result.offset,
      hasMore: result.offset + result.limit < result.total,
    },
  };
};

export const sendSuccess = <T>(res: Response, data: T, statusCode: number = 200): void => {
  res.status(statusCode).json(formatResponse(data));
};

export const sendPaginatedSuccess = <T>(
  res: Response,
  result: PaginatedResult<T>,
  statusCode: number = 200
): void => {
  res.status(statusCode).json(formatPaginatedResponse(result));
};

export const sendError = (
  res: Response,
  statusCode: number,
  code: string,
  message: string,
  errors?: Record<string, string[]>
): void => {
  res.status(statusCode).json({
    success: false,
    error: {
      code,
      message,
      ...(errors && { errors }),
    },
  });
};

