     1→import express, { Express, Request, Response } from 'express';
     2→import 'express-async-errors';
     3→import helmet from 'helmet';
     4→import cors from 'cors';
     5→import { rateLimit } from 'express-rate-limit';
     6→import pinoHttp from 'pino-http';
     7→import config from './config/index.js';
     8→import logger from './config/logger.js';
     9→import { NotFoundError, TooManyRequestsError } from './utils/errors.js';
    10→
    11→const app: Express = express();
    12→
    13→// Security Headers
    14→app.use(
    15→  helmet({
    16→    contentSecurityPolicy: {
    17→      directives: {
    18→        defaultSrc: ["'self'"],
    19→        styleSrc: ["'self'", "'unsafe-inline'"], // For Swagger UI
    20→        scriptSrc: ["'self'", "'unsafe-inline'"], // For Swagger UI
    21→        imgSrc: ["'self'", 'data:'],
    22→      },
    23→    },
    24→    crossOriginEmbedderPolicy: false, // For Swagger UI
    25→  })
    26→);
    27→
    28→// CORS Configuration
    29→app.use(
    30→  cors({
    31→    origin: config.security.corsOrigins,
    32→    methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
    33→    allowedHeaders: ['Content-Type', 'Authorization'],
    34→    credentials: true,
    35→    maxAge: 86400, // 24 hours
    36→  })
    37→);
    38→
    39→// Rate Limiting
    40→const limiter = rateLimit({
    41→  windowMs: config.security.rateLimitWindowMs,
    42→  max: config.security.rateLimitMax,
    43→  standardHeaders: true,
    44→  legacyHeaders: false,
    45→  handler: (req, res) => {
    46→    const resetTime = req.rateLimit.resetTime;
    47→    const retryAfter = resetTime ? Math.ceil((resetTime.getTime() - Date.now()) / 1000) : 60;
    48→    const error = new TooManyRequestsError(retryAfter);
    49→    res.status(error.statusCode).json({
    50→      success: false,
    51→      error: {
    52→        code: error.code,
    53→        message: error.message,
    54→        retryAfter: error.retryAfter,
    55→      },
    56→    });
    57→  },
    58→});
    59→
    60→app.use('/api', limiter);
    61→
    62→// Body Parsing
    63→app.use(express.json({ limit: '10kb' }));
    64→app.use(express.urlencoded({ extended: true, limit: '10kb' }));
    65→
    66→// Request Logging
    67→app.use(
    68→  pinoHttp({
    69→    logger,
    70→    customLogLevel: (req, res, error) => {
    71→      if (res.statusCode >= 500) return 'error';
    72→      if (res.statusCode >= 400) return 'warn';
    73→      return 'info';
    74→    },
    75→    customSuccessMessage: (req, res) => {
    76→      return `${req.method} ${req.url} ${res.statusCode}`;
    77→    },
    78→    customErrorMessage: (req, res, error) => {
    79→      return `${req.method} ${req.url} ${res.statusCode} - ${error.message}`;
    80→    },
    81→  })
    82→);
    83→
    84→// Health Check (Public - before auth)
    85→app.get('/health', async (req: Request, res: Response) => {
    86→  const { getPrismaClient } = await import('./config/database.js');
    87→  const prisma = getPrismaClient();
    88→
    89→  try {
    90→    await prisma.$queryRaw`SELECT 1`;
    91→    res.status(200).json({
    92→      success: true,
    93→      data: {
    94→        status: 'healthy',
    95→        timestamp: new Date().toISOString(),
    96→        uptime: process.uptime(),
    97→        database: 'connected',
    98→      },
    99→    });
   100→  } catch (error) {
   101→    res.status(503).json({
   102→      success: false,
   103→      error: {
   104→        code: 'SERVICE_UNAVAILABLE',
   105→        message: 'Database connection failed',
