## 3. Component Design

### 3.1 Task Repository

**File**: `src/repositories/task.repository.ts`

```typescript
import { PrismaClient, Task, TaskStatus, Prisma } from '@prisma/client';
import { PaginatedResult } from '../types/api.types.js';

// Phase 1: System user ID for anonymous tasks
export const SYSTEM_USER_ID = '00000000-0000-0000-0000-000000000000';

export interface CreateTaskData {
  title: string;
  description?: string | null;
  status?: TaskStatus;
}

export interface UpdateTaskData {
  title?: string;
  description?: string | null;
  status?: TaskStatus;
}

export interface TaskQueryFilters {
  status?: TaskStatus;
  sortBy?: 'createdAt' | 'updatedAt';
  order?: 'asc' | 'desc';
  limit: number;
  offset: number;
}

export class TaskRepository {
  constructor(private prisma: PrismaClient) {}

  async create(data: CreateTaskData): Promise<Task> {
    return this.prisma.task.create({
      data: {
        title: data.title,
        description: data.description ?? null,
        status: data.status ?? 'PENDING',
        priority: 'MEDIUM',
        userId: SYSTEM_USER_ID,
      },
    });
  }

  async findById(id: string): Promise<Task | null> {
    return this.prisma.task.findUnique({
      where: { id },
    });
  }

  async findMany(filters: TaskQueryFilters): Promise<PaginatedResult<Task>> {
    const where: Prisma.TaskWhereInput = {};

    if (filters.status) {
      where.status = filters.status;
    }

    const orderBy: Prisma.TaskOrderByWithRelationInput = {
      [filters.sortBy || 'createdAt']: filters.order || 'desc',
    };

    const [data, total] = await Promise.all([
      this.prisma.task.findMany({
        where,
        orderBy,
        skip: filters.offset,
        take: filters.limit,
      }),
      this.prisma.task.count({ where }),
    ]);

    return {
      data,
      total,
      limit: filters.limit,
      offset: filters.offset,
    };
  }

  async update(id: string, data: UpdateTaskData): Promise<Task> {
    return this.prisma.task.update({
      where: { id },
      data,
    });
  }

  async delete(id: string): Promise<Task> {
    return this.prisma.task.delete({
      where: { id },
    });
  }

  async exists(id: string): Promise<boolean> {
    const count = await this.prisma.task.count({
      where: { id },
    });
    return count > 0;
  }
}
```

### 3.2 Task Service

**File**: `src/services/task.service.ts`

```typescript
import { Task, TaskStatus } from '@prisma/client';
import { TaskRepository, CreateTaskData, UpdateTaskData, TaskQueryFilters } from '../repositories/task.repository.js';
import { NotFoundError, ValidationError } from '../utils/errors.js';
import { PaginatedResult } from '../types/api.types.js';

export interface CreateTaskInput {
  title: string;
  description?: string;
  status?: TaskStatus;
}

export interface UpdateTaskInput {
  title?: string;
  description?: string | null;
  status?: TaskStatus;
}

export interface ListTasksInput {
  status?: TaskStatus;
  sortBy?: 'createdAt' | 'updatedAt';
  order?: 'asc' | 'desc';
  limit: number;
  offset: number;
}

export class TaskService {
  constructor(private taskRepository: TaskRepository) {}

  async create(input: CreateTaskInput): Promise<Task> {
    const data: CreateTaskData = {
      title: input.title.trim(),
      description: input.description?.trim() || null,
      status: input.status,
    };

    return this.taskRepository.create(data);
  }

  async findById(id: string): Promise<Task> {
    const task = await this.taskRepository.findById(id);

    if (!task) {
      throw new NotFoundError('Task');
    }

    return task;
  }

  async findAll(input: ListTasksInput): Promise<PaginatedResult<Task>> {
    const filters: TaskQueryFilters = {
      status: input.status,
      sortBy: input.sortBy || 'createdAt',
      order: input.order || 'desc',
      limit: input.limit,
      offset: input.offset,
    };

    return this.taskRepository.findMany(filters);
  }

  async update(id: string, input: UpdateTaskInput): Promise<Task> {
    // Check if task exists
    const exists = await this.taskRepository.exists(id);
    if (!exists) {
      throw new NotFoundError('Task');
    }

    // Check if at least one field is provided
    const hasUpdates = Object.values(input).some((value) => value !== undefined);
    if (!hasUpdates) {
      throw new ValidationError('No update fields provided');
    }

    // Build update data
    const data: UpdateTaskData = {};

    if (input.title !== undefined) {
      data.title = input.title.trim();
    }

    if (input.description !== undefined) {
      data.description = input.description === null ? null : input.description.trim();
    }

    if (input.status !== undefined) {
      data.status = input.status;
    }

    return this.taskRepository.update(id, data);
  }

  async delete(id: string): Promise<void> {
    // Check if task exists
    const exists = await this.taskRepository.exists(id);
    if (!exists) {
      throw new NotFoundError('Task');
    }

    await this.taskRepository.delete(id);
  }
}
```

### 3.3 Task Controller

**File**: `src/controllers/task.controller.ts`

```typescript
import { Request, Response } from 'express';
import { TaskService, CreateTaskInput, UpdateTaskInput, ListTasksInput } from '../services/task.service.js';
import { sendSuccess, sendPaginatedSuccess } from '../utils/response.js';
import { parsePagination } from '../utils/pagination.js';
import { TaskStatus } from '@prisma/client';

export class TaskController {
  constructor(private taskService: TaskService) {}

  create = async (req: Request, res: Response): Promise<void> => {
    const input: CreateTaskInput = {
      title: req.body.title,
      description: req.body.description,
      status: req.body.status,
    };

    const task = await this.taskService.create(input);
    sendSuccess(res, this.formatTaskResponse(task), 201);
  };

  findById = async (req: Request, res: Response): Promise<void> => {
    const { id } = req.params;
    const task = await this.taskService.findById(id);
    sendSuccess(res, this.formatTaskResponse(task));
  };

  findAll = async (req: Request, res: Response): Promise<void> => {
    const { limit, offset } = parsePagination(req.query);

    const input: ListTasksInput = {
      status: req.query.status as TaskStatus | undefined,
      sortBy: (req.query.sortBy as 'createdAt' | 'updatedAt') || 'createdAt',
      order: (req.query.order as 'asc' | 'desc') || 'desc',
      limit,
      offset,
    };

    const result = await this.taskService.findAll(input);

    // Format each task in the result
    const formattedResult = {
      ...result,
      data: result.data.map(this.formatTaskResponse),
    };

    sendPaginatedSuccess(res, formattedResult);
  };

  update = async (req: Request, res: Response): Promise<void> => {
    const { id } = req.params;

    const input: UpdateTaskInput = {
      title: req.body.title,
      description: req.body.description,
      status: req.body.status,
    };

    const task = await this.taskService.update(id, input);
    sendSuccess(res, this.formatTaskResponse(task));
  };

  delete = async (req: Request, res: Response): Promise<void> => {
    const { id } = req.params;
    await this.taskService.delete(id);
    res.status(204).send();
  };

  // Format task response (Phase 1: exclude priority, dueDate, userId, assigneeId)
  private formatTaskResponse = (task: any): any => ({
    id: task.id,
    title: task.title,
    description: task.description,
    status: task.status.toLowerCase(),  // Convert enum to lowercase
    createdAt: task.createdAt.toISOString(),
    updatedAt: task.updatedAt.toISOString(),
  });
}
```

### 3.4 Task Validators

**File**: `src/validators/task.validator.ts`

```typescript
import { body, param, query, ValidationChain } from 'express-validator';

// Valid status values
const VALID_STATUSES = ['pending', 'in_progress', 'completed'];

// UUID v4 regex pattern
const UUID_REGEX = /^[0-9a-f]{8}-[0-9a-f]{4}-4[0-9a-f]{3}-[89ab][0-9a-f]{3}-[0-9a-f]{12}$/i;

/**
 * Validator for task ID parameter
 */
export const taskIdValidator: ValidationChain[] = [
  param('id')
    .trim()
    .custom((value) => {
      if (!UUID_REGEX.test(value)) {
        throw new Error('Invalid task ID format');
      }
      return true;
    }),
];

/**
 * Validator for creating a task
 */
export const createTaskValidator: ValidationChain[] = [
  body('title')
    .exists({ checkFalsy: false, checkNull: true })
    .withMessage('Title is required')
    .bail()
    .isString()
    .withMessage('Title must be a string')
    .bail()
    .trim()
    .isLength({ min: 1 })
    .withMessage('Title must be at least 1 character')
    .bail()
    .isLength({ max: 200 })
    .withMessage('Title must be at most 200 characters')
    .escape(), // XSS prevention

  body('description')
    .optional({ nullable: true })
    .isString()
    .withMessage('Description must be a string')
    .bail()
    .trim()
    .isLength({ max: 2000 })
    .withMessage('Description must be at most 2000 characters')
    .escape(), // XSS prevention

  body('status')
    .optional()
    .isString()
    .withMessage('Status must be a string')
    .bail()
    .isIn(VALID_STATUSES)
    .withMessage('Status must be one of: pending, in_progress, completed'),
];

/**
 * Validator for updating a task
 */
export const updateTaskValidator: ValidationChain[] = [
  ...taskIdValidator,

  body('title')
    .optional()
    .isString()
    .withMessage('Title must be a string')
    .bail()
    .trim()
    .isLength({ min: 1 })
    .withMessage('Title must be at least 1 character')
    .bail()
    .isLength({ max: 200 })
    .withMessage('Title must be at most 200 characters')
    .escape(),

  body('description')
    .optional({ nullable: true })
    .custom((value) => {
      if (value === null) return true;
      if (typeof value !== 'string') {
        throw new Error('Description must be a string or null');
      }
      return true;
    })
    .bail()
    .customSanitizer((value) => (value === null ? null : String(value).trim()))
    .custom((value) => {
      if (value !== null && value.length > 2000) {
        throw new Error('Description must be at most 2000 characters');
      }
      return true;
    }),

  body('status')
    .optional()
    .isString()
    .withMessage('Status must be a string')
    .bail()
    .isIn(VALID_STATUSES)
    .withMessage('Status must be one of: pending, in_progress, completed'),
];

/**
 * Validator for listing tasks with filters
 */
export const listTasksValidator: ValidationChain[] = [
  query('status')
    .optional()
    .isString()
    .withMessage('Status must be a string')
    .bail()
    .isIn(VALID_STATUSES)
    .withMessage('Status must be one of: pending, in_progress, completed'),

  query('limit')
    .optional()
    .isInt({ min: 1, max: 100 })
    .withMessage('Limit must be between 1 and 100')
    .toInt(),

  query('offset')
    .optional()
    .isInt({ min: 0 })
    .withMessage('Offset must be a non-negative integer')
    .toInt(),

  query('sortBy')
    .optional()
    .isIn(['createdAt', 'updatedAt'])
    .withMessage('sortBy must be one of: createdAt, updatedAt'),

  query('order')
    .optional()
    .isIn(['asc', 'desc'])
    .withMessage('order must be one of: asc, desc'),
];

/**
 * Validator for delete operation (just ID validation)
 */
export const deleteTaskValidator: ValidationChain[] = [...taskIdValidator];
```

### 3.5 Validation Middleware

**File**: `src/middleware/validate.middleware.ts`

```typescript
import { Request, Response, NextFunction } from 'express';
import { validationResult, ValidationChain, ValidationError as ExpressValidationError } from 'express-validator';
import { ValidationError } from '../utils/errors.js';

/**
 * Format express-validator errors into our API error format
 */
const formatValidationErrors = (errors: ExpressValidationError[]): Record<string, string[]> => {
  const formatted: Record<string, string[]> = {};

  for (const error of errors) {
    // Handle different error types
    const field = error.type === 'field' ? error.path : 'general';

    if (!formatted[field]) {
      formatted[field] = [];
    }

    formatted[field].push(error.msg);
  }

  return formatted;
};

/**
 * Middleware factory that runs validation chains and formats errors
 */
export const validate = (validations: ValidationChain[]) => {
  return async (req: Request, res: Response, next: NextFunction): Promise<void> => {
    // Run all validations
    await Promise.all(validations.map((validation) => validation.run(req)));

    // Check for errors
    const errors = validationResult(req);

    if (errors.isEmpty()) {
      next();
      return;
    }

    // Format and throw validation error
    const formattedErrors = formatValidationErrors(errors.array());

    // Get first error message as main message
    const firstError = errors.array()[0];
    const message = firstError.msg || 'Validation failed';

    throw new ValidationError(message, formattedErrors);
  };
};
```

