import { TaskService } from '../../../src/services/task.service';
import { TaskRepository } from '../../../src/repositories/task.repository';
import { NotFoundError, ValidationError } from '../../../src/utils/errors';

describe('TaskService', () => {
  let taskService: TaskService;
  let mockRepository: jest.Mocked<TaskRepository>;

  beforeEach(() => {
    mockRepository = {
      create: jest.fn(),
      findById: jest.fn(),
      findMany: jest.fn(),
      update: jest.fn(),
      delete: jest.fn(),
      exists: jest.fn(),
    } as unknown as jest.Mocked<TaskRepository>;

    taskService = new TaskService(mockRepository);
  });

  describe('create', () => {
    it('should create a task with valid data', async () => {
      const input = { title: 'Test Task', description: 'Description' };
      const expectedTask = { id: 'uuid', ...input, status: 'PENDING' };

      mockRepository.create.mockResolvedValue(expectedTask as any);

      const result = await taskService.create(input);

      expect(result).toEqual(expectedTask);
      expect(mockRepository.create).toHaveBeenCalledWith({
        title: 'Test Task',
        description: 'Description',
        status: undefined,
      });
    });

    it('should trim whitespace from title', async () => {
      const input = { title: '  Test Task  ' };
      mockRepository.create.mockResolvedValue({ id: 'uuid', title: 'Test Task' } as any);

      await taskService.create(input);

      expect(mockRepository.create).toHaveBeenCalledWith(
        expect.objectContaining({ title: 'Test Task' })
      );
    });
  });

  describe('findById', () => {
    it('should return task when found', async () => {
      const task = { id: 'uuid', title: 'Test' };
      mockRepository.findById.mockResolvedValue(task as any);

      const result = await taskService.findById('uuid');

      expect(result).toEqual(task);
    });

    it('should throw NotFoundError when task does not exist', async () => {
      mockRepository.findById.mockResolvedValue(null);

      await expect(taskService.findById('uuid')).rejects.toThrow(NotFoundError);
    });
  });

  describe('update', () => {
    it('should update task when exists', async () => {
      mockRepository.exists.mockResolvedValue(true);
      mockRepository.update.mockResolvedValue({ id: 'uuid', title: 'Updated' } as any);

      const result = await taskService.update('uuid', { title: 'Updated' });

      expect(result.title).toBe('Updated');
    });

    it('should throw NotFoundError when task does not exist', async () => {
      mockRepository.exists.mockResolvedValue(false);

      await expect(taskService.update('uuid', { title: 'Test' })).rejects.toThrow(NotFoundError);
    });

    it('should throw ValidationError when no fields provided', async () => {
      mockRepository.exists.mockResolvedValue(true);

      await expect(taskService.update('uuid', {})).rejects.toThrow(ValidationError);
    });
  });

  describe('delete', () => {
    it('should delete task when exists', async () => {
      mockRepository.exists.mockResolvedValue(true);
      mockRepository.delete.mockResolvedValue({} as any);

      await expect(taskService.delete('uuid')).resolves.not.toThrow();
    });

    it('should throw NotFoundError when task does not exist', async () => {
      mockRepository.exists.mockResolvedValue(false);

      await expect(taskService.delete('uuid')).rejects.toThrow(NotFoundError);
    });
  });
});
