export interface PaginationParams {
  limit?: string | number;
  offset?: string | number;
}

export interface ParsedPagination {
  limit: number;
  offset: number;
}

const DEFAULT_LIMIT = 20;
const MAX_LIMIT = 100;

export const parsePagination = (params: PaginationParams): ParsedPagination => {
  const limit = Math.min(
    Math.max(parseInt(String(params.limit || DEFAULT_LIMIT), 10), 1),
    MAX_LIMIT
  );

  const offset = Math.max(parseInt(String(params.offset || 0), 10), 0);

  return { limit, offset };
};

