import express, { Express, Request, Response } from 'express';
import 'express-async-errors';
import helmet from 'helmet';
import cors from 'cors';
import { rateLimit } from 'express-rate-limit';
import pinoHttp from 'pino-http';
import config from './config/index.js';
import logger from './config/logger.js';
import { NotFoundError, TooManyRequestsError } from './utils/errors.js';

const app: Express = express();

// Security Headers
app.use(
  helmet({
    contentSecurityPolicy: {
      directives: {
        defaultSrc: ["'self'"],
        styleSrc: ["'self'", "'unsafe-inline'"], // For Swagger UI
        scriptSrc: ["'self'", "'unsafe-inline'"], // For Swagger UI
        imgSrc: ["'self'", 'data:'],
      },
    },
    crossOriginEmbedderPolicy: false, // For Swagger UI
  })
);

// CORS Configuration
app.use(
  cors({
    origin: config.security.corsOrigins,
    methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
    allowedHeaders: ['Content-Type', 'Authorization'],
    credentials: true,
    maxAge: 86400, // 24 hours
  })
);

// Rate Limiting
const limiter = rateLimit({
  windowMs: config.security.rateLimitWindowMs,
  max: config.security.rateLimitMax,
  standardHeaders: true,
  legacyHeaders: false,
  handler: (req, res) => {
    const resetTime = req.rateLimit.resetTime;
    const retryAfter = resetTime ? Math.ceil((resetTime.getTime() - Date.now()) / 1000) : 60;
    const error = new TooManyRequestsError(retryAfter);
    res.status(error.statusCode).json({
      success: false,
      error: {
        code: error.code,
        message: error.message,
        retryAfter: error.retryAfter,
      },
    });
  },
});

app.use('/api', limiter);

// Body Parsing
app.use(express.json({ limit: '10kb' }));
app.use(express.urlencoded({ extended: true, limit: '10kb' }));

// Request Logging
app.use(
  pinoHttp({
    logger,
    customLogLevel: (req, res, error) => {
      if (res.statusCode >= 500) return 'error';
      if (res.statusCode >= 400) return 'warn';
      return 'info';
    },
    customSuccessMessage: (req, res) => {
      return `${req.method} ${req.url} ${res.statusCode}`;
    },
    customErrorMessage: (req, res, error) => {
      return `${req.method} ${req.url} ${res.statusCode} - ${error.message}`;
    },
  })
);

// Health Check (Public - before auth)
app.get('/health', async (req: Request, res: Response) => {
  const { getPrismaClient } = await import('./config/database.js');
  const prisma = getPrismaClient();

  try {
    await prisma.$queryRaw`SELECT 1`;
    res.status(200).json({
      success: true,
      data: {
        status: 'healthy',
        timestamp: new Date().toISOString(),
        uptime: process.uptime(),
        database: 'connected',
      },
    });
  } catch (error) {
    res.status(503).json({
      success: false,
      error: {
        code: 'SERVICE_UNAVAILABLE',
        message: 'Database connection failed',
      },
    });
  }
});

// API Routes (will be added in implementation phase)
// app.use('/api/auth', authRoutes);
// app.use('/api/tasks', authenticate, taskRoutes);

// API Documentation (will be added in production readiness phase)
// app.use('/api-docs', swaggerUi.serve, swaggerUi.setup(swaggerSpec));

// 404 Handler
app.use((req: Request, res: Response) => {
  const error = new NotFoundError('Route');
  res.status(error.statusCode).json({
    success: false,
    error: {
      code: error.code,
      message: error.message,
    },
  });
});

// Global Error Handler (will be imported from middleware in implementation phase)
// app.use(errorHandler);

export default app;

