```typescript
// src/routes/task.routes.ts

import { Router } from 'express';
import { getTaskController } from '../controllers/task.controller.js';
import { validate } from '../middleware/validate.middleware.js';
import {
  validateCreateTask,
  validateUpdateTask,
  validateTaskId,
  validateListTasks,
} from '../validators/task.validator.js';

const router = Router();
const taskController = getTaskController();

/**
 * POST /api/tasks
 * Create a new task
 */
router.post(
  '/',
  validateCreateTask,
  validate,
  taskController.create
);

/**
 * GET /api/tasks
 * Get all tasks with optional filtering and pagination
 */
router.get(
  '/',
  validateListTasks,
  validate,
  taskController.getAll
);

/**
 * GET /api/tasks/:id
 * Get a single task by ID
 */
router.get(
  '/:id',
  validateTaskId,
  validate,
  taskController.getById
);

/**
 * PUT /api/tasks/:id
 * Update a task by ID
 */
router.put(
  '/:id',
  [...validateTaskId, ...validateUpdateTask],
  validate,
  taskController.update
);

/**
 * DELETE /api/tasks/:id
 * Delete a task by ID
 */
router.delete(
  '/:id',
  validateTaskId,
  validate,
  taskController.delete
);

export default router;
