    it('should return task when found', async () => {
      const task = { id: 'uuid-123', title: 'Test' };
      mockRepository.findByIdOrThrow.mockResolvedValue(task as any);

      const result = await taskService.getById('uuid-123');

      expect(result).toEqual(task);
    });

    it('should throw NotFoundError when not found', async () => {
      mockRepository.findByIdOrThrow.mockRejectedValue(new NotFoundError('Task'));

      await expect(taskService.getById('uuid-123')).rejects.toThrow(NotFoundError);
    });
  });

  describe('getAll', () => {
    it('should pass filters to repository', async () => {
      mockRepository.findMany.mockResolvedValue({
        data: [],
        total: 0,
        limit: 20,
        offset: 0,
      });

      await taskService.getAll({
        status: 'pending',
        limit: 10,
        offset: 5,
        sortBy: 'createdAt',
        order: 'asc',
      });

      expect(mockRepository.findMany).toHaveBeenCalledWith({
        status: TaskStatus.PENDING,
        limit: 10,
        offset: 5,
        sortBy: 'createdAt',
        order: 'asc',
      });
    });
  });

  describe('delete', () => {
    it('should call repository delete', async () => {
      mockRepository.delete.mockResolvedValue(undefined);

      await taskService.delete('uuid-123');

      expect(mockRepository.delete).toHaveBeenCalledWith('uuid-123');
    });
  });
});
