        data: {
          title: 'Original Title',
          description: 'Original Description',
          status: TaskStatus.PENDING,
        },
      });
      taskId = task.id;
    });

    it('should update a task', async () => {
      const response = await request(app)
        .put(`/api/tasks/${taskId}`)
        .send({ title: 'Updated Title' })
        .expect(200);

      expect(response.body.data.title).toBe('Updated Title');
      expect(response.body.data.description).toBe('Original Description');
    });

    it('should update status', async () => {
      const response = await request(app)
        .put(`/api/tasks/${taskId}`)
        .send({ status: 'completed' })
        .expect(200);

      expect(response.body.data.status).toBe('completed');
    });

    it('should set description to null', async () => {
      const response = await request(app)
        .put(`/api/tasks/${taskId}`)
        .send({ description: null })
        .expect(200);

      expect(response.body.data.description).toBeNull();
    });

    it('should return 400 for empty body', async () => {
      const response = await request(app)
        .put(`/api/tasks/${taskId}`)
        .send({})
        .expect(400);

      expect(response.body.error.message).toBe('No update fields provided');
    });

    it('should return 404 for non-existent task', async () => {
      const fakeId = '00000000-0000-0000-0000-000000000000';
      const response = await request(app)
        .put(`/api/tasks/${fakeId}`)
        .send({ title: 'Update' })
        .expect(404);

      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should update updatedAt timestamp', async () => {
      const original = await prisma.task.findUnique({ where: { id: taskId } });

      // Wait a bit to ensure timestamp difference
      await new Promise((resolve) => setTimeout(resolve, 100));

      const response = await request(app)
        .put(`/api/tasks/${taskId}`)
        .send({ title: 'Updated' })
        .expect(200);

      expect(new Date(response.body.data.updatedAt).getTime())
        .toBeGreaterThan(new Date(original!.updatedAt).getTime());
    });
  });

  describe('DELETE /api/tasks/:id', () => {
    let taskId: string;

    beforeEach(async () => {
      const task = await prisma.task.create({
        data: { title: 'To Delete', status: TaskStatus.PENDING },
      });
      taskId = task.id;
    });

    it('should delete a task', async () => {
