// tests/helpers/db.ts

import { PrismaClient, TaskStatus } from '@prisma/client';
import { getPrismaClient } from '../../src/config/database';

let prisma: PrismaClient;

export const getTestPrisma = (): PrismaClient => {
  if (!prisma) {
    prisma = getPrismaClient();
  }
  return prisma;
};

export const clearDatabase = async (): Promise<void> => {
  const client = getTestPrisma();
  await client.task.deleteMany();
  // Add other tables as needed in Phase 2
};

export interface SeedTaskInput {
  title: string;
  description?: string;
  status?: TaskStatus;
}

export const seedTasks = async (tasks: SeedTaskInput[]): Promise<void> => {
  const client = getTestPrisma();
  await client.task.createMany({
    data: tasks.map((task) => ({
      title: task.title,
      description: task.description ?? null,
      status: task.status ?? TaskStatus.PENDING,
    })),
  });
};

export const createTask = async (data: SeedTaskInput) => {
  const client = getTestPrisma();
  return client.task.create({
    data: {
      title: data.title,
      description: data.description ?? null,
      status: data.status ?? TaskStatus.PENDING,
    },
  });
};
