   338→```typescript
   339→import { Task, TaskStatus } from '@prisma/client';
   340→import { TaskRepository, CreateTaskData, UpdateTaskData, TaskQueryFilters } from '../repositories/task.repository.js';
   341→import { NotFoundError, ValidationError } from '../utils/errors.js';
   342→import { PaginatedResult } from '../types/api.types.js';
   343→
   344→export interface CreateTaskInput {
   345→  title: string;
   346→  description?: string;
   347→  status?: TaskStatus;
   348→}
   349→
   350→export interface UpdateTaskInput {
   351→  title?: string;
   352→  description?: string | null;
   353→  status?: TaskStatus;
   354→}
   355→
   356→export interface ListTasksInput {
   357→  status?: TaskStatus;
   358→  sortBy?: 'createdAt' | 'updatedAt';
   359→  order?: 'asc' | 'desc';
   360→  limit: number;
   361→  offset: number;
   362→}
   363→
   364→export class TaskService {
   365→  constructor(private taskRepository: TaskRepository) {}
   366→
   367→  async create(input: CreateTaskInput): Promise<Task> {
   368→    const data: CreateTaskData = {
   369→      title: input.title.trim(),
   370→      description: input.description?.trim() || null,
   371→      status: input.status,
   372→    };
   373→
   374→    return this.taskRepository.create(data);
   375→  }
   376→
   377→  async findById(id: string): Promise<Task> {
   378→    const task = await this.taskRepository.findById(id);
   379→
   380→    if (!task) {
   381→      throw new NotFoundError('Task');
   382→    }
   383→
   384→    return task;
   385→  }
   386→
   387→  async findAll(input: ListTasksInput): Promise<PaginatedResult<Task>> {
   388→    const filters: TaskQueryFilters = {
   389→      status: input.status,
   390→      sortBy: input.sortBy || 'createdAt',
   391→      order: input.order || 'desc',
   392→      limit: input.limit,
   393→      offset: input.offset,
   394→    };
   395→
   396→    return this.taskRepository.findMany(filters);
   397→  }
   398→
   399→  async update(id: string, input: UpdateTaskInput): Promise<Task> {
   400→    // Check if task exists
   401→    const exists = await this.taskRepository.exists(id);
   402→    if (!exists) {
   403→      throw new NotFoundError('Task');
   404→    }
   405→
   406→    // Check if at least one field is provided
   407→    const hasUpdates = Object.values(input).some((value) => value !== undefined);
   408→    if (!hasUpdates) {
   409→      throw new ValidationError('No update fields provided');
   410→    }
   411→
   412→    // Build update data
   413→    const data: UpdateTaskData = {};
   414→
   415→    if (input.title !== undefined) {
   416→      data.title = input.title.trim();
   417→    }
   418→
   419→    if (input.description !== undefined) {
   420→      data.description = input.description === null ? null : input.description.trim();
   421→    }
   422→
   423→    if (input.status !== undefined) {
   424→      data.status = input.status;
   425→    }
   426→
   427→    return this.taskRepository.update(id, data);
   428→  }
   429→
   430→  async delete(id: string): Promise<void> {
   431→    // Check if task exists
   432→    const exists = await this.taskRepository.exists(id);
   433→    if (!exists) {
   434→      throw new NotFoundError('Task');
   435→    }
   436→
   437→    await this.taskRepository.delete(id);
   438→  }
   439→}
