   678→### 3.5 Validation Middleware
   679→
   680→**File**: `src/middleware/validate.middleware.ts`
   681→
   682→```typescript
   683→import { Request, Response, NextFunction } from 'express';
   684→import { validationResult, ValidationChain, ValidationError as ExpressValidationError } from 'express-validator';
   685→import { ValidationError } from '../utils/errors.js';
   686→
   687→/**
   688→ * Format express-validator errors into our API error format
   689→ */
   690→const formatValidationErrors = (errors: ExpressValidationError[]): Record<string, string[]> => {
   691→  const formatted: Record<string, string[]> = {};
   692→
   693→  for (const error of errors) {
   694→    // Handle different error types
   695→    const field = error.type === 'field' ? error.path : 'general';
   696→
   697→    if (!formatted[field]) {
   698→      formatted[field] = [];
   699→    }
   700→
   701→    formatted[field].push(error.msg);
   702→  }
   703→
   704→  return formatted;
   705→};
   706→
   707→/**
   708→ * Middleware factory that runs validation chains and formats errors
   709→ */
   710→export const validate = (validations: ValidationChain[]) => {
   711→  return async (req: Request, res: Response, next: NextFunction): Promise<void> => {
   712→    // Run all validations
   713→    await Promise.all(validations.map((validation) => validation.run(req)));
   714→
   715→    // Check for errors
   716→    const errors = validationResult(req);
   717→
   718→    if (errors.isEmpty()) {
   719→      next();
   720→      return;
   721→    }
   722→
   723→    // Format and throw validation error
   724→    const formattedErrors = formatValidationErrors(errors.array());
   725→
   726→    // Get first error message as main message
   727→    const firstError = errors.array()[0];
   728→    const message = firstError.msg || 'Validation failed';
   729→
   730→    throw new ValidationError(message, formattedErrors);
   731→  };
   732→};
   733→```
