   879→### 3.8 Error Middleware (Global Handler)
   880→
   881→**File**: `src/middleware/error.middleware.ts`
   882→
   883→```typescript
   884→import { Request, Response, NextFunction } from 'express';
   885→import { Prisma } from '@prisma/client';
   886→import { AppError, ValidationError, TooManyRequestsError, NotFoundError, InternalServerError } from '../utils/errors.js';
   887→import logger from '../config/logger.js';
   888→
   889→/**
   890→ * Sanitize headers for logging (remove sensitive data)
   891→ */
   892→const sanitizeHeaders = (headers: Record<string, any>): Record<string, any> => {
   893→  const sanitized = { ...headers };
   894→  delete sanitized.authorization;
   895→  delete sanitized.cookie;
   896→  return sanitized;
   897→};
   898→
   899→/**
   900→ * Sanitize body for logging (remove sensitive data)
   901→ */
   902→const sanitizeBody = (body: unknown): unknown => {
   903→  if (typeof body !== 'object' || body === null) return body;
   904→  const sanitized = { ...(body as Record<string, unknown>) };
   905→  delete sanitized.password;
   906→  delete sanitized.token;
   907→  return sanitized;
   908→};
   909→
   910→/**
   911→ * Handle Prisma-specific errors and convert to AppError
   912→ */
   913→const handlePrismaError = (error: Prisma.PrismaClientKnownRequestError): AppError => {
   914→  switch (error.code) {
   915→    case 'P2002':
   916→      // Unique constraint violation
   917→      return new ValidationError('A record with this value already exists');
   918→
   919→    case 'P2025':
   920→      // Record not found
   921→      return new NotFoundError('Resource');
   922→
   923→    case 'P2003':
   924→      // Foreign key constraint violation
   925→      return new ValidationError('Related resource not found');
   926→
   927→    case 'P2014':
   928→      // Required relation violation
   929→      return new ValidationError('Required relation constraint violation');
   930→
   931→    default:
   932→      logger.error({ prismaCode: error.code, meta: error.meta }, 'Unhandled Prisma error');
   933→      return new InternalServerError('Database operation failed');
   934→  }
   935→};
   936→
   937→/**
   938→ * Global error handler middleware
   939→ */
   940→export const errorHandler = (
   941→  error: Error,
   942→  req: Request,
   943→  res: Response,
   944→  next: NextFunction
   945→): void => {
   946→  // Log error (but not in test environment for cleaner output)
   947→  if (process.env.NODE_ENV !== 'test') {
   948→    logger.error({
   949→      err: {
   950→        name: error.name,
   951→        message: error.message,
   952→        stack: error.stack,
   953→      },
   954→      req: {
   955→        method: req.method,
   956→        url: req.url,
   957→        headers: sanitizeHeaders(req.headers as Record<string, any>),
   958→        body: sanitizeBody(req.body),
   959→      },
   960→    });
   961→  }
   962→
   963→  // Handle known application errors
   964→  if (error instanceof AppError) {
   965→    const response: Record<string, any> = {
   966→      success: false,
   967→      error: {
   968→        code: error.code,
   969→        message: error.message,
   970→      },
   971→    };
   972→
   973→    // Add validation errors if present
   974→    if (error instanceof ValidationError && error.errors) {
   975→      response.error.errors = error.errors;
   976→    }
   977→
   978→    // Add retryAfter if present
   979→    if (error instanceof TooManyRequestsError) {
   980→      response.error.retryAfter = error.retryAfter;
   981→      res.setHeader('Retry-After', String(error.retryAfter));
   982→    }
   983→
   984→    res.status(error.statusCode).json(response);
   985→    return;
   986→  }
   987→
   988→  // Handle Prisma errors
   989→  if (error instanceof Prisma.PrismaClientKnownRequestError) {
   990→    const appError = handlePrismaError(error);
   991→    res.status(appError.statusCode).json({
   992→      success: false,
   993→      error: {
   994→        code: appError.code,
   995→        message: appError.message,
   996→      },
   997→    });
   998→    return;
   999→  }
  1000→
  1001→  // Handle Prisma validation errors
  1002→  if (error instanceof Prisma.PrismaClientValidationError) {
  1003→    res.status(400).json({
  1004→      success: false,
  1005→      error: {
  1006→        code: 'VALIDATION_ERROR',
  1007→        message: 'Invalid data provided',
  1008→      },
  1009→    });
  1010→    return;
  1011→  }
  1012→
  1013→  // Handle JSON parsing errors
  1014→  if (error instanceof SyntaxError && 'body' in error) {
  1015→    res.status(400).json({
  1016→      success: false,
  1017→      error: {
  1018→        code: 'BAD_REQUEST',
  1019→        message: 'Invalid JSON in request body',
  1020→      },
  1021→    });
  1022→    return;
  1023→  }
  1024→
  1025→  // Handle unexpected errors
  1026→  res.status(500).json({
  1027→    success: false,
  1028→    error: {
  1029→      code: 'INTERNAL_ERROR',
  1030→      message: process.env.NODE_ENV === 'production'
  1031→        ? 'An unexpected error occurred'
  1032→        : error.message,
  1033→    },
  1034→  });
  1035→};
  1036→```
