### 3.8 Error Middleware (Global Handler)

**File**: `src/middleware/error.middleware.ts`

```typescript
import { Request, Response, NextFunction } from 'express';
import { Prisma } from '@prisma/client';
import { AppError, ValidationError, TooManyRequestsError, NotFoundError, InternalServerError } from '../utils/errors.js';
import logger from '../config/logger.js';

/**
 * Sanitize headers for logging (remove sensitive data)
 */
const sanitizeHeaders = (headers: Record<string, any>): Record<string, any> => {
  const sanitized = { ...headers };
  delete sanitized.authorization;
  delete sanitized.cookie;
  return sanitized;
};

/**
 * Sanitize body for logging (remove sensitive data)
 */
const sanitizeBody = (body: unknown): unknown => {
  if (typeof body !== 'object' || body === null) return body;
  const sanitized = { ...(body as Record<string, unknown>) };
  delete sanitized.password;
  delete sanitized.token;
  return sanitized;
};

/**
 * Handle Prisma-specific errors and convert to AppError
 */
const handlePrismaError = (error: Prisma.PrismaClientKnownRequestError): AppError => {
  switch (error.code) {
    case 'P2002':
      // Unique constraint violation
      return new ValidationError('A record with this value already exists');

    case 'P2025':
      // Record not found
      return new NotFoundError('Resource');

    case 'P2003':
      // Foreign key constraint violation
      return new ValidationError('Related resource not found');

    case 'P2014':
      // Required relation violation
      return new ValidationError('Required relation constraint violation');

    default:
      logger.error({ prismaCode: error.code, meta: error.meta }, 'Unhandled Prisma error');
      return new InternalServerError('Database operation failed');
  }
};

/**
 * Global error handler middleware
 */
export const errorHandler = (
  error: Error,
  req: Request,
  res: Response,
  next: NextFunction
): void => {
  // Log error (but not in test environment for cleaner output)
  if (process.env.NODE_ENV !== 'test') {
    logger.error({
      err: {
        name: error.name,
        message: error.message,
        stack: error.stack,
      },
      req: {
        method: req.method,
        url: req.url,
        headers: sanitizeHeaders(req.headers as Record<string, any>),
        body: sanitizeBody(req.body),
      },
    });
  }

  // Handle known application errors
  if (error instanceof AppError) {
    const response: Record<string, any> = {
      success: false,
      error: {
        code: error.code,
        message: error.message,
      },
    };

    // Add validation errors if present
    if (error instanceof ValidationError && error.errors) {
      response.error.errors = error.errors;
    }

    // Add retryAfter if present
    if (error instanceof TooManyRequestsError) {
      response.error.retryAfter = error.retryAfter;
      res.setHeader('Retry-After', String(error.retryAfter));
    }

    res.status(error.statusCode).json(response);
    return;
  }

  // Handle Prisma errors
  if (error instanceof Prisma.PrismaClientKnownRequestError) {
    const appError = handlePrismaError(error);
    res.status(appError.statusCode).json({
      success: false,
      error: {
        code: appError.code,
        message: appError.message,
      },
    });
    return;
  }

  // Handle Prisma validation errors
  if (error instanceof Prisma.PrismaClientValidationError) {
    res.status(400).json({
      success: false,
      error: {
        code: 'VALIDATION_ERROR',
        message: 'Invalid data provided',
      },
    });
    return;
  }

  // Handle JSON parsing errors
  if (error instanceof SyntaxError && 'body' in error) {
    res.status(400).json({
      success: false,
      error: {
        code: 'BAD_REQUEST',
        message: 'Invalid JSON in request body',
      },
    });
    return;
  }

  // Handle unexpected errors
  res.status(500).json({
    success: false,
    error: {
      code: 'INTERNAL_ERROR',
      message: process.env.NODE_ENV === 'production'
        ? 'An unexpected error occurred'
        : error.message,
    },
  });
};
