      const input = { title: 'Test Task', description: 'Description' };
      const expectedTask = { id: 'uuid', ...input, status: 'PENDING' };

      mockRepository.create.mockResolvedValue(expectedTask as any);

      const result = await taskService.create(input);

      expect(result).toEqual(expectedTask);
      expect(mockRepository.create).toHaveBeenCalledWith({
