        email: 'system@taskapi.local',
        password: 'no-login',
        name: 'System User',
      },
    });
  });

  afterAll(async () => {
    await disconnectPrisma();
  });

  beforeEach(async () => {
    await prisma.task.deleteMany();
  });

  describe('POST /api/tasks', () => {
    it('should create a task with valid data', async () => {
      const response = await request(app)
        .post('/api/tasks')
        .send({ title: 'Test Task', description: 'Description' })
        .expect(201);

      expect(response.body.success).toBe(true);
      expect(response.body.data.title).toBe('Test Task');
      expect(response.body.data.status).toBe('pending');
      expect(response.body.data.id).toBeDefined();
    });

    it('should return 400 when title is missing', async () => {
      const response = await request(app)
        .post('/api/tasks')
        .send({ description: 'Description' })
        .expect(400);

      expect(response.body.success).toBe(false);
      expect(response.body.error.code).toBe('VALIDATION_ERROR');
      expect(response.body.error.errors.title).toContain('Title is required');
    });

    it('should return 400 when title exceeds 200 characters', async () => {
      const response = await request(app)
        .post('/api/tasks')
        .send({ title: 'a'.repeat(201) })
        .expect(400);

      expect(response.body.error.errors.title).toContain('Title must be at most 200 characters');
    });

    it('should escape HTML in title for XSS prevention', async () => {
      const response = await request(app)
        .post('/api/tasks')
        .send({ title: '<script>alert("xss")</script>' })
        .expect(201);

      expect(response.body.data.title).not.toContain('<script>');
    });
  });

  describe('GET /api/tasks', () => {
    it('should return empty array when no tasks exist', async () => {
      const response = await request(app)
        .get('/api/tasks')
        .expect(200);

      expect(response.body.success).toBe(true);
      expect(response.body.data).toEqual([]);
      expect(response.body.pagination.total).toBe(0);
    });

    it('should return tasks with pagination', async () => {
      // Create 25 tasks
      for (let i = 0; i < 25; i++) {
        await prisma.task.create({
          data: {
            title: `Task ${i}`,
            userId: SYSTEM_USER_ID,
            priority: 'MEDIUM',
          },
        });
      }

      const response = await request(app)
        .get('/api/tasks')
        .expect(200);

      expect(response.body.data.length).toBe(20); // Default limit
      expect(response.body.pagination.total).toBe(25);
      expect(response.body.pagination.hasMore).toBe(true);
    });

    it('should filter by status', async () => {
      await prisma.task.create({
        data: { title: 'Pending', status: 'PENDING', userId: SYSTEM_USER_ID, priority: 'MEDIUM' },
      });
      await prisma.task.create({
        data: { title: 'Completed', status: 'COMPLETED', userId: SYSTEM_USER_ID, priority: 'MEDIUM' },
      });

      const response = await request(app)
        .get('/api/tasks?status=pending')
        .expect(200);

      expect(response.body.data.length).toBe(1);
      expect(response.body.data[0].title).toBe('Pending');
    });
  });

  describe('GET /api/tasks/:id', () => {
    it('should return task by ID', async () => {
      const task = await prisma.task.create({
        data: { title: 'Test', userId: SYSTEM_USER_ID, priority: 'MEDIUM' },
      });

      const response = await request(app)
        .get(`/api/tasks/${task.id}`)
        .expect(200);

      expect(response.body.data.id).toBe(task.id);
    });

    it('should return 404 for non-existent ID', async () => {
      const response = await request(app)
        .get('/api/tasks/00000000-0000-4000-8000-000000000000')
        .expect(404);

      expect(response.body.error.code).toBe('NOT_FOUND');
    });

    it('should return 400 for invalid UUID format', async () => {
      const response = await request(app)
        .get('/api/tasks/invalid-id')
        .expect(400);

      expect(response.body.error.code).toBe('VALIDATION_ERROR');
    });
  });

  describe('PUT /api/tasks/:id', () => {
    it('should update task', async () => {
      const task = await prisma.task.create({
        data: { title: 'Original', userId: SYSTEM_USER_ID, priority: 'MEDIUM' },
      });

      const response = await request(app)
        .put(`/api/tasks/${task.id}`)
        .send({ title: 'Updated' })
        .expect(200);

      expect(response.body.data.title).toBe('Updated');
    });

    it('should allow setting description to null', async () => {
      const task = await prisma.task.create({
        data: { title: 'Test', description: 'Original', userId: SYSTEM_USER_ID, priority: 'MEDIUM' },
      });

      const response = await request(app)
        .put(`/api/tasks/${task.id}`)
        .send({ description: null })
        .expect(200);

      expect(response.body.data.description).toBeNull();
    });

    it('should return 400 for empty body', async () => {
      const task = await prisma.task.create({
        data: { title: 'Test', userId: SYSTEM_USER_ID, priority: 'MEDIUM' },
      });

      const response = await request(app)
        .put(`/api/tasks/${task.id}`)
        .send({})
        .expect(400);

      expect(response.body.error.message).toBe('No update fields provided');
    });
  });

  describe('DELETE /api/tasks/:id', () => {
    it('should delete task', async () => {
      const task = await prisma.task.create({
        data: { title: 'Test', userId: SYSTEM_USER_ID, priority: 'MEDIUM' },
      });

      await request(app)
        .delete(`/api/tasks/${task.id}`)
        .expect(204);

      const deleted = await prisma.task.findUnique({ where: { id: task.id } });
      expect(deleted).toBeNull();
    });

    it('should return 404 for non-existent ID', async () => {
      const response = await request(app)
        .delete('/api/tasks/00000000-0000-4000-8000-000000000000')
        .expect(404);

      expect(response.body.error.code).toBe('NOT_FOUND');
    });
  });
});
